update.if:
update product
set product_status = #{dataStatus}
where product_type = #{dataType} 
	  and product_no = #{dataNo}

update users
set status = #{dataStatus}
where users_no = #{dataNo}

update recipe
set recipe_status = #{dataStatus}
where food_no = #{dataNo}

update review
set review_status = #{dataStatus}
where review_no = #{dataNo}

update board
set board_status = #{dataStatus}
where board_no = #{dataNo}

mapper.select:
select *
from product
where product_no = #{pNo}

mapper.update:
update product set product_price = #{productPrice},
product_option = #{productOption},
product_stock = #{productStock}, product_modify_date = sysdate, product_sale = #{productSale}, product_status = #{productStatus} where product_no = #{productNo}

mapper.select:
select *
from (select *
	  from image
	  order by image_no asc)	
where image_divide_no = #{imageDivideNo} and image_type = #{imageType}

	  and image_level = #{imageLevel}

mapper.delete:
delete image
where image_type = #{imageType} and image_divide_no = ${imageDivideNo} 

	and image_level = #{imageLevel}

mapper.select:
select *
from users
where users_no = #{users_no}

select count(*) from follow	where following_user_no = #{usersNo}
union all
select count(*) from follow where users_no = #{usersNo}
union all
select count(*) from recipe where users_no = #{usersNo}
union all
select count(*) from bookmark where users_no = #{usersNo}

		and number_type = #{bookmarkType}

union all
select count(*) from likes where users_no = #{usersNo}

		and number_type = #{likeType}

select *
from menu
where product_no = #{pNo}

mapper.update:
update menu
set menu_name = #{menuName},
	menu_type = #{menuType},
	menu_kind = #{menuKind},
	menu_content = #{menuContent}
where product_no = #{productNo}

mapper.select:
select *
from ingredient
where ingredient_no = #{igdNo}

mapper.update:
update ingredient set ingredient_name = #{ingredientName}, ingredient_type = #{ingredientType}, ingredient_content = #{ingredientContent},
product_no = #{productNo},
is_accept = #{isAccept} where ingredient_no = #{ingredientNo}

update ingredient
set is_accept = #{isAccept}
where ingredient_no = #{ingredientNo}

mapper.select:
select * from food where food_no in (select food_no from food group by food_no having count(*) = 1)
and food_kind = #{kind}
and food_type = #{type}
order by food_name

select * 
from food

	where product_no = #{no}


	where food_no = #{no}

mapper.update:
update food
set food_name = #{foodName},
	food_kind = #{foodKind},
	food_type = #{foodType},
	food_content = #{foodContent}
where product_no = #{productNo}

mapper.select:
select count(*)
from tool 


		where tool_name like '%${searchText}%'

			and tool_type = ${type}





		where tool_type = ${type}

select * from (select * from tool order by product_no desc)
where tool_name like '%${searchText}%'
and tool_type = ${type}
where tool_type = ${type}

select *
from tool
where product_no = #{toolNo}

mapper.update:
update tool
set tool_name = #{toolName},
	tool_type = #{toolType},
	tool_content = #{toolContent}
where product_no = #{productNo}

mapper.select:
select count(*) from review left join recipe on(product_no = food_no) left join board on(product_no = board_no) left join product using(product_no) left join food using(product_no) left join menu using(product_no) left join ingredient using(product_no) left join tool using(product_no)
where order_no = ${kind}
where not order_no = -1
and order_no = 0
and not order_no = 0 and product_type = ${type}

select * from review left join recipe on(product_no = food_no) left join board on(product_no = board_no) left join product using(product_no) left join food using(product_no) left join menu using(product_no) left join ingredient using(product_no) left join tool using(product_no)
where order_no = ${kind}
where not order_no in (0, -1)
and product_type = ${type}
where not order_no = -1
and review_writer like '%${searchText}%'
and review_content like '%${searchText}%'
and review_no = ${number}
and product_no = ${number}
order by review_no desc

mapper.delete:
delete review

	where order_no = ${kind}
		  and product_no = ${number} 


	where order_no > 0
		  and order_no = ${number}  


	where review_no = ${number}
