mapper.update:
update board
set board_count = board_count + 1
where board_no = #{bId}

update review
set review_status = 'N'
where review_writer = #{reviewWriter}
	and review_no = #{reviewNo}	
	and product_no = #{productNo}	
	and order_no = -1

update review
set review_content = #{reviewContent}
where review_writer = #{reviewWriter}
	and review_no = #{reviewNo}
	and product_no = #{productNo}
	and review_status = 'Y'
	and order_no = -1
