mapper.select:
select *
from orders
where product_no = #{productNo} and users_no = #{usersNo}

mapper.update:
update review
set review_content = #{reviewContent}, review_score = #{reviewScore}
where review_no = #{reviewNo}

mapper.select:
select count(*)
from qna
where qna_status='Y' and product_no=#{mNo}

select *
from image
where image_type = 1 and image_level = 0 and image_divide_no = #{usersNo}

select count(*)
from menu
where menu_kind = #{cate}
